    private final MotionMagicVoltage m_motionMagicReq = new MotionMagicVoltage(0).withSlot(0);
    TalonFXConfiguration climbConf = new TalonFXConfiguration();

    /**
    *
    */
    public ClimbSubsystem() {
        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
        raiser = new TalonFX(26, TunerConstants.kCANBus);
        // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=CONSTRUCTORS
        armBottomSwitch = new DigitalInput(9);
        raiserUpperTarget = Constants.ClimbConstants.raiserUpperTarget;
        raiserLowerTarget = Constants.ClimbConstants.raiserLowerTarget;
        climbing = false;
        isOnTower = false;

        // Configs
        // CurrentLimitsConfigs climbCurrent = climbConf.CurrentLimits;
        // climbCurrent.StatorCurrentLimit = Constants.ClimbConstants.climbCurrentLimit;
        MotorOutputConfigs climbOutput = climbConf.MotorOutput;
        // climbOutput.Inverted = InvertedValue.CounterClockwise_Positive;
        climbOutput.NeutralMode = NeutralModeValue.Brake;

        climbConf.Slot0.kP = 0.4; // An error of 1 rotation results in 2.4 V output
        climbConf.Slot0.kI = 0.0075; // No output for integrated error
        climbConf.Slot0.kD = 0; // A velocity of 1 rps results in 0.1 V output
        climbConf.Slot0.kS = 0.17558;
        climbConf.Slot0.kV = 0.11202;
        climbConf.Slot0.kA = 0.0024753;

        // climbConf.Slot0.GravityType = GravityTypeValue.Arm_Cosine;

        // // Peak output of 8 V
        climbConf.Voltage.withPeakForwardVoltage(Volts.of(10)).withPeakReverseVoltage(Volts.of(-10));

        MotionMagicConfigs motionMagicOn = climbConf.MotionMagic;
        motionMagicOn.withMotionMagicCruiseVelocity(RotationsPerSecond.of(499))
                .withMotionMagicAcceleration(RotationsPerSecondPerSecond.of(100))
                .withMotionMagicJerk(RotationsPerSecondPerSecond.per(Second).of(0)); // 0 makes it fast

        /* Retry config apply up to 5 times, report if failure */
        StatusCode status = StatusCode.StatusCodeNotInitialized;
        for (int i = 0; i < 5; ++i) {
            status = raiser.getConfigurator().apply(climbConf);
            if (status.isOK())
                break;
        }
        if (!status.isOK()) {
            System.out.println("Could not apply configs, error code: " + status.toString());
        }

        SmartDashboard.putData("Subsystem: Climb", this);
    }

    @Override
    public void periodic() {
        // This method will be called once per scheduler run
        SmartDashboard.putBoolean("Arm is raised", climbing);
        SmartDashboard.putBoolean("Arm Bottom Switch is triggered", getBottomSwitch());
        SmartDashboard.putString("Lockdown Stage", currentLockdownMode.name());
        SmartDashboard.putNumber("Climb Stage", climbStage);
        SmartDashboard.putNumber("Height Positions", raiser.getPosition().getValueAsDouble());
        setLockdownMode();
        setLockdownDriveControl();
    }

