    public boolean isRaised() {
        return Math.abs(raiser.getPosition().getValueAsDouble() - raiserUpperTarget) < .3;
    }

    public boolean isLowered() {
        return Math.abs(raiser.getPosition().getValueAsDouble() - raiserLowerTarget) < .3;
    }

